
'use strict'

var assert = require('assert')
var html = require('../lib')

describe('html-parse', function () {
  it('should parse nodes', function () {
    let tree = html.parse('<script>')
    assert.equal(tree[0].type, 'script')
    assert.equal(typeof tree[0].children, 'object')
  })

  it('should traverse into each node', function () {
    let tree = html.parse('<div><p>a</p></div><form></form>')
    let i = 0

    html.traverse(tree, function (node) {
      if (i == 0) assert.equal(node.name, 'div')
      if (i == 1) assert.equal(node.name, 'p')
      if (i == 2) assert.equal(node.name, 'form')
      i++
    })
  })
})